import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import api from "../services/api";
import { useAuth } from "../context/AuthContext";

export default function Dashboard() {
  const { user } = useAuth();
  const [reservations, setReservations] = useState([]);

  useEffect(() => {
    const fetch = async () => {
      try {
        const catways = await api.get("/catways");
        if (catways.data.length === 0) {
          setReservations([]);
          return;
        }
        const first = catways.data[0];
        const res = await api.get(`/catways/${first.catwayNumber}/reservations`);
        setReservations(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetch();
  }, []);

  return (
    <div>
      <Navbar />
      <main className="container my-4">
        <div className="row mb-4">
          <div className="col-12 text-center text-md-start">
            <h1 className="mb-2">Tableau de bord</h1>
            <p className="mb-1">
              <strong>Utilisateur connecté :</strong> {user?.username} — {user?.email}
            </p>
            <p className="mb-0"><strong>Date :</strong> {new Date().toLocaleDateString()}</p>
          </div>
        </div>

        <h2 className="mb-3">Réservations (exemple)</h2>

        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-dark">
              <tr>
                <th>Catway</th>
                <th>Client</th>
                <th>Bateau</th>
                <th>Début</th>
                <th>Fin</th>
              </tr>
            </thead>
            <tbody>
              {reservations.length > 0 ? (
                reservations.map(r => (
                  <tr key={r._id}>
                    <td>{r.catwayNumber}</td>
                    <td>{r.clientName}</td>
                    <td>{r.boatName}</td>
                    <td>{new Date(r.startDate).toLocaleDateString()}</td>
                    <td>{new Date(r.endDate).toLocaleDateString()}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="text-center">Aucune réservation disponible</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
}
