import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import api from "../services/api";

export default function ReservationsCRUD() {
  const [reservations, setReservations] = useState([]);
  const [editing, setEditing] = useState(null);
  const [form, setForm] = useState({ catwayNumber: "", clientName: "", boatName: "", startDate: "", endDate: "" });
  const [catways, setCatways] = useState([]);

  const fetch = async () => {
    try {
      const c = await api.get("/catways");
      setCatways(c.data);
      let all = [];
      for (const cw of c.data) {
        const r = await api.get(`/catways/${cw.catwayNumber}/reservations`);
        all = all.concat(r.data);
      }
      setReservations(all);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => { fetch(); }, []);

  useEffect(() => {
    if (editing) setForm({
      catwayNumber: editing.catwayNumber,
      clientName: editing.clientName,
      boatName: editing.boatName,
      startDate: editing.startDate?.split("T")[0],
      endDate: editing.endDate?.split("T")[0]
    });
    else setForm({ catwayNumber: "", clientName: "", boatName: "", startDate: "", endDate: "" });
  }, [editing]);

  const submit = async (e) => {
    e.preventDefault();
    if (editing) {
      await api.put(`/catways/${form.catwayNumber}/reservations/${editing._id}`, {
        clientName: form.clientName,
        boatName: form.boatName,
        startDate: form.startDate,
        endDate: form.endDate
      });
      setEditing(null);
    } else {
      await api.post(`/catways/${form.catwayNumber}/reservations`, form);
    }
    setForm({ catwayNumber: "", clientName: "", boatName: "", startDate: "", endDate: "" });
    fetch();
  };

  const handleDelete = async (r) => {
    if (!window.confirm("Supprimer cette réservation ?")) return;
    await api.delete(`/catways/${r.catwayNumber}/reservations/${r._id}`);
    fetch();
  };

  return (
    <div style={{ backgroundColor: "#f8f9fa", minHeight: "100vh" }}>
      <Navbar />
      <main className="container my-4">
        <h1 className="mb-4 text-center" style={{ color: "#20c997" }}>Gestion des Réservations</h1>

        <div className="card p-3 mb-4 shadow-sm border-0">
          <h3 className="mb-3" style={{ color: "#20c997" }}>{editing ? "Modifier réservation" : "Créer réservation"}</h3>
          <form onSubmit={submit} className="row g-3">
            <div className="col-12 col-md-4">
              <select className="form-select" value={form.catwayNumber} onChange={e => setForm({...form, catwayNumber: e.target.value})} required>
                <option value="">Sélectionner catway</option>
                {catways.map(c => <option key={c.catwayNumber} value={c.catwayNumber}>{c.catwayNumber} - {c.catwayType}</option>)}
              </select>
            </div>
            <div className="col-12 col-md-4">
              <input className="form-control" placeholder="Nom client" value={form.clientName} onChange={e => setForm({...form, clientName: e.target.value})} required />
            </div>
            <div className="col-12 col-md-4">
              <input className="form-control" placeholder="Nom bateau" value={form.boatName} onChange={e => setForm({...form, boatName: e.target.value})} required />
            </div>
            <div className="col-12 col-md-6">
              <label>Date début</label>
              <input type="date" className="form-control" value={form.startDate} onChange={e => setForm({...form, startDate: e.target.value})} required />
            </div>
            <div className="col-12 col-md-6">
              <label>Date fin</label>
              <input type="date" className="form-control" value={form.endDate} onChange={e => setForm({...form, endDate: e.target.value})} required />
            </div>
            <div className="col-12 d-flex gap-2 flex-wrap">
              <button type="submit" className="btn btn-info rounded-pill">{editing ? "Mettre à jour" : "Créer"}</button>
              {editing && <button type="button" className="btn btn-secondary rounded-pill" onClick={() => setEditing(null)}>Annuler</button>}
            </div>
          </form>
        </div>

        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-dark">
              <tr>
                <th>Catway</th><th>Client</th><th>Bateau</th><th>Début</th><th>Fin</th><th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {reservations.map(r => (
                <tr key={r._id}>
                  <td>{r.catwayNumber}</td>
                  <td>{r.clientName}</td>
                  <td>{r.boatName}</td>
                  <td>{new Date(r.startDate).toLocaleDateString()}</td>
                  <td>{new Date(r.endDate).toLocaleDateString()}</td>
                  <td className="d-flex flex-wrap gap-1">
                    <button className="btn btn-warning btn-sm rounded-pill" onClick={() => setEditing(r)}>Modifier</button>
                    <button className="btn btn-danger btn-sm rounded-pill" onClick={() => handleDelete(r)}>Supprimer</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
}
