import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import api from "../services/api";

export default function UsersCRUD() {
  const [users, setUsers] = useState([]);
  const [editing, setEditing] = useState(null);
  const [form, setForm] = useState({ username: "", email: "", password: "" });

  const fetchUsers = async () => {
    const res = await api.get("/users");
    setUsers(res.data);
  };

  useEffect(() => { fetchUsers(); }, []);

  useEffect(() => {
    if (editing) setForm({ username: editing.username, email: editing.email, password: "" });
    else setForm({ username: "", email: "", password: "" });
  }, [editing]);

  const submit = async (e) => {
    e.preventDefault();
    if (editing) {
      await api.put(`/users/${editing.email}`, { username: form.username, password: form.password || undefined });
      setEditing(null);
    } else {
      await api.post("/users", form);
    }
    setForm({ username: "", email: "", password: "" });
    fetchUsers();
  };

  const handleDelete = async (email) => {
    if (!window.confirm("Supprimer cet utilisateur ?")) return;
    await api.delete(`/users/${email}`);
    fetchUsers();
  };

  return (
    <div>
      <Navbar />
      <main className="container my-4">
        <h1 className="mb-4 text-center">Gestion des utilisateurs</h1>

        {/* Formulaire */}
        <div className="card p-3 mb-4 shadow-sm">
          <h3 className="mb-3">{editing ? "Modifier utilisateur" : "Créer utilisateur"}</h3>
          <form onSubmit={submit} className="row g-3">
            <div className="col-12 col-md-4">
              <input
                type="text"
                className="form-control"
                placeholder="Nom"
                value={form.username}
                onChange={e => setForm({ ...form, username: e.target.value })}
                required
              />
            </div>

            <div className="col-12 col-md-4">
              <input
                type="email"
                className="form-control"
                placeholder="Email"
                value={form.email}
                onChange={e => setForm({ ...form, email: e.target.value })}
                required
                disabled={editing} // empêche de modifier l’email lors de l’édition
              />
            </div>

            <div className="col-12 col-md-4">
              <input
                type="password"
                className="form-control"
                placeholder="Mot de passe"
                value={form.password}
                onChange={e => setForm({ ...form, password: e.target.value })}
              />
            </div>

            <div className="col-12 d-flex gap-2">
              <button type="submit" className="btn btn-primary">{editing ? "Mettre à jour" : "Créer"}</button>
              {editing && <button type="button" className="btn btn-secondary" onClick={() => setEditing(null)}>Annuler</button>}
            </div>
          </form>
        </div>

        {/* Tableau */}
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-dark">
              <tr>
                <th>Nom</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.length > 0 ? (
                users.map(u => (
                  <tr key={u.email}>
                    <td>{u.username}</td>
                    <td>{u.email}</td>
                    <td className="d-flex flex-wrap gap-1">
                      <button className="btn btn-sm btn-warning" onClick={() => setEditing(u)}>Modifier</button>
                      <button className="btn btn-sm btn-danger" onClick={() => handleDelete(u.email)}>Supprimer</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3" className="text-center">Aucun utilisateur</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
}
