import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import api from "../services/api";
import { useAuth } from "../context/AuthContext";

export default function Dashboard() {
  const { user } = useAuth();
  const [reservations, setReservations] = useState([]);

  useEffect(() => {
    const fetch = async () => {
      try {
        const catways = await api.get("/catways");
        if (catways.data.length === 0) {
          setReservations([]);
          return;
        }
        const first = catways.data[0];
        const res = await api.get(`/catways/${first.catwayNumber}/reservations`);
        setReservations(res.data);
      } catch (err) { console.error(err); }
    };
    fetch();
  }, []);

  return (
    <div style={{ backgroundColor: "#f8f9fa", minHeight: "100vh" }}>
      <Navbar />
      <main className="container my-4">
        <h1 className="mb-4 text-center" style={{ color: "#20c997" }}>Tableau de bord</h1>

        <p>Utilisateur connecté : {user?.username} — {user?.email}</p>
        <p>Date : {new Date().toLocaleDateString()}</p>

        <h2 className="mt-4" style={{ color: "#20c997" }}>Réservations (exemple)</h2>
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-dark">
              <tr>
                <th>Catway</th><th>Client</th><th>Bateau</th><th>Début</th><th>Fin</th>
              </tr>
            </thead>
            <tbody>
              {reservations.length > 0 ? reservations.map(r => (
                <tr key={r._id}>
                  <td>{r.catwayNumber}</td>
                  <td>{r.clientName}</td>
                  <td>{r.boatName}</td>
                  <td>{new Date(r.startDate).toLocaleDateString()}</td>
                  <td>{new Date(r.endDate).toLocaleDateString()}</td>
                </tr>
              )) : (
                <tr><td colSpan="5" className="text-center text-muted">Aucune réservation</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
}
